drop table if exists books;
create table books (
	id int(8) NOT NULL AUTO_INCREMENT,
	title varchar(255),
	author varchar(255),
	date varchar(10),
	genres varchar(255),
	characters text,
    synopsis text,
    PRIMARY KEY (id)
);

